import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

// Create the AuthContext
const AuthContext = createContext();

// Provider component
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState("");

  // Function to log in user and store token
  const login = async (email, password) => {
    try {
      const response = await axios.post("http://localhost:3000/auth/login", {
        email,
        password,
      });
      if (response.status === 200) {
        const { role, accessToken, refreshToken } = response.data;
        localStorage.setItem("accessToken", accessToken);
        localStorage.setItem("refreshToken", refreshToken);
        localStorage.setItem("role", role);
        setIsAuthenticated(true);
        setUserRole(role);
      }
    } catch (error) {
      console.error("Login failed:", error);
      throw new Error(error.response?.data?.message || "Login failed");
    }
  };

  // Logout function to clear tokens
  const logout = () => {
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    localStorage.removeItem("role");
    setIsAuthenticated(false);
    setUserRole("");
  };

  // Function to refresh token if expired
  const refreshAccessToken = async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    if (refreshToken) {
      try {
        const response = await axios.post(
          "http://localhost:3000/auth/refresh",
          {
            token: refreshToken,
          }
        );
        const { accessToken } = response.data;
        localStorage.setItem("accessToken", accessToken);
        return accessToken;
      } catch (error) {
        console.error("Token refresh failed:", error);
        console.error("Error details:", error.response?.data);
        logout(); // Call logout if the refresh fails
      }
    }
  };
  

  // Check authentication status on load or token expiration
  const checkAuthStatus = async () => {
    let token = localStorage.getItem("accessToken");

    // If token exists but is expired, attempt to refresh it
    if (token && isTokenExpired(token)) {
      token = await refreshAccessToken();
    }

    if (token) {
      setIsAuthenticated(true);
    } else {
      setIsAuthenticated(false);
    }
  };

  // Utility to check if token is expired
  const isTokenExpired = (token) => {
    try {
      const parts = token.split(".");
      if (parts.length !== 3) {
        throw new Error("Token does not have 3 parts");
      }
      const decoded = JSON.parse(atob(parts[1])); // Decode payload
      return decoded.exp * 1000 < Date.now();
    } catch (error) {
      console.error("Error checking token expiration:", error);
      return true; // Assume expired if there's an error
    }
  };

  // Initialize auth status on component mount
  useEffect(() => {
    checkAuthStatus();
  }, []);

  return (
    <AuthContext.Provider
      value={{ isAuthenticated, userRole, login, logout, checkAuthStatus }}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use the AuthContext
export const useAuth = () => {
  return useContext(AuthContext);
};
