import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    username: "",
    name: "",
    email: "",
    password: "",
    selectedSkills: [], // Changed to an array to handle multiple selections
  });
  const [skills, setSkills] = useState([]); // List of skills fetched from the API
  const [filteredSkills, setFilteredSkills] = useState([]); // Filtered list for search
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState(""); // Track the search term
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch skills from the API
    const fetchSkills = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/skills");
        setSkills(response.data);
        setFilteredSkills(response.data); // Initialize filtered list with all skills
      } catch (err) {
        console.error("Error fetching skills:", err);
        setError("Error fetching skills.");
      }
    };

    fetchSkills();
  }, []);

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    // Filter skills based on the search term
    const filtered = skills.filter((skill) =>
      skill.name.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredSkills(filtered);
  };

  const handleSkillChange = (e) => {
    const { value, checked } = e.target;

    // Update selected skills based on checkbox selection
    if (checked) {
      setFormData({
        ...formData,
        selectedSkills: [...formData.selectedSkills, value],
      });
    } else {
      setFormData({
        ...formData,
        selectedSkills: formData.selectedSkills.filter(
          (skill) => skill !== value
        ),
      });
    }
  };

  const handleChange = (e) => {
    setError(null); // Reset error on input change
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form Data", formData);
    try {
      // Send the registration data, including selected skills
      await axios.post("http://localhost:3000/auth/register", formData, {
        withCredentials: true,
      });
      console.log("User registered successfully");
      setFormData({
        username: "",
        name: "",
        email: "",
        password: "",
        selectedSkills: [], // Reset skills after successful registration
      });
      navigate("/login"); // Redirect to login page after successful registration
    } catch (err) {
      console.error("Error registering user:", err);
      const errorMessage =
        err.response?.data?.message || "An error occurred during registration.";
      setError(errorMessage);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-blue-200 px-6 py-12">
      <div className="bg-white w-full sm:w-4/12 shadow-lg rounded-lg p-6">
        <div className="sm:mx-auto sm:w-full sm:max-w-sm mt-10">
          <img
            className="mx-auto h-10 w-auto"
            src={require("../Logo/CareerGate.png")}
            alt="Your Company"
          />
          <h2 className="mt-5 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
            Register Your Account
          </h2>
        </div>
        <div className="mt-6 sm:mx-auto sm:w-full sm:max-w-sm">
          <form className="space-y-6" onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700">
                Search and Select Skills
              </label>
              <input
                type="text"
                placeholder="Search skills"
                value={searchTerm}
                onChange={handleSearchChange}
                className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              />
              <div className="mt-2 space-y-2">
                {filteredSkills.length > 0 ? (
                  filteredSkills.map((skill) => (
                    <div key={skill.id} className="flex items-center">
                      <input
                        type="checkbox"
                        id={skill.id}
                        name="selectedSkills"
                        value={skill.name}
                        onChange={handleSkillChange}
                        checked={formData.selectedSkills.includes(skill.name)}
                        className="mr-2"
                      />
                      <label
                        htmlFor={skill.id}
                        className="text-sm text-gray-700">
                        {skill.name}
                      </label>
                    </div>
                  ))
                ) : (
                  <div>No skills found</div>
                )}
              </div>
            </div>

            <div className="mb-4">
              <label
                htmlFor="username"
                className="block text-sm font-medium text-gray-700">
                Username
              </label>
              <input
                type="text"
                name="username"
                id="username"
                value={formData.username}
                onChange={handleChange}
                required
                className="form-input mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>

            <div className="mb-4">
              <label
                htmlFor="name"
                className="block text-sm font-medium text-gray-700">
                Full Name
              </label>
              <input
                type="text"
                name="name"
                id="name"
                value={formData.name}
                onChange={handleChange}
                required
                className="form-input mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>

            <div className="mb-4">
              <label
                htmlFor="email"
                className="block text-sm font-medium text-gray-700">
                Email Address
              </label>
              <input
                type="email"
                name="email"
                id="email"
                value={formData.email}
                onChange={handleChange}
                required
                className="form-input mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>

            <div className="mb-6">
              <label
                htmlFor="password"
                className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <input
                type="password"
                name="password"
                id="password"
                value={formData.password}
                onChange={handleChange}
                required
                className="form-input mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>

            {error && <div className="text-red-500 text-sm mb-4">{error}</div>}

            <div>
              <button
                type="submit"
                className="w-full py-2 px-4 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75">
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegistrationForm;
