import React, { useState, useContext } from "react";
import Layout from "../components/Layout";
import Internship from "../components/Internship";
import { DataContext } from "../Contexts/Data-context";

const InternShips = () => {
  const { internships } = useContext(DataContext);
  // State to store the internships data
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Empty dependency array ensures the effect runs only once

  if (loading) return <p>Loading internships...</p>;
  if (error) return <p>{error}</p>;

  return (
    <Layout>
      <Internship internships={internships} />
      {/* Pass the internships data to the Internship component */}
    </Layout>
  );
};

export default InternShips;
