import React from "react";

function Internship({ internships }) {
  // Check if internships is defined and is an array before using .length
  const hasInternships = Array.isArray(internships) && internships.length > 0;

  return (
    <div className="py-8 px-4">
      <h2 className="text-2xl font-semibold text-center mb-6">Available Internships</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {hasInternships ? (
          internships.map((internship) => (
            <div
              key={internship._id}
              className="bg-white border rounded-lg shadow-lg p-6 hover:shadow-xl transition-shadow duration-300"
            >
              <h3 className="text-xl font-semibold text-gray-800">{internship.title}</h3>
              <p className="text-gray-600">{internship.company}</p>
              <p className="text-gray-500">{internship.location}</p>
              <p className="text-gray-700 mt-2">{internship.description}</p>
              <p className="mt-4 text-gray-800 font-medium">Salary: {internship.salary}</p>
              <p className="mt-2 text-gray-500 text-sm">
                Posted On: {new Date(internship.postedAt).toLocaleDateString()}
              </p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-600">No internships available at the moment.</p>
        )}
      </div>
    </div>
  );
}

export default Internship;
