import React, { useState, useEffect } from "react";
import About from "./About";
import JobsCategory from "./JobsCategory";
import JobsGrid from "./JobsGrid";
import { Swiper, SwiperSlide } from "swiper/react";
// import User from "./UserLogin";
function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false);
  // const [showDetails, setShowDetails] = useState(false);
  // const user = {
  //   name: "John Doe",
  //   profilePicture: "https://example.com/profile.jpg",
  //   // Add more user details here
  // };
  // const handleUserClick = (value) => {
  //   setShowDetails(value);
  // };
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  const testimonials = [
    {
      id: 1,
      quote: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: "url_to_image_1.jpg",
      name: "John Doe",
      jobPosition: "Software Engineer",
    },
    {
      id: 2,
      quote:
        "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris.",
      image: "url_to_image_2.jpg",
      name: "Jane Smith",
      jobPosition: "UX Designer",
    },
    {
      id: 3,
      quote:
        "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris.",
      image: "url_to_image_2.jpg",
      name: "Will Smith",
      jobPosition: "UI Designer",
    },
    // Add more testimonials as needed
  ];
  const handlePaginationClick = (swiper) => {
    const activeIndex = swiper.activeIndex;

    // Change the order of testimonials based on the activeIndex
    let reorderedTestimonials = testimonials.slice(activeIndex);
    reorderedTestimonials = reorderedTestimonials.concat(
      testimonials.slice(0, activeIndex)
    );

    setTestimonials(reorderedTestimonials);
  };

  return (
    <>
      <div>
        <header
          className={`${
            isScrolled ? "bg-blue-400" : "bg-tranparent bg-opacity-50"
          } fixed top-0 z-50 w-full transition-all`}>
          <nav>
            <div className="mx-auto max-w-7xl px-2 sm:px-6 lg:px-8">
              <div className="relative flex h-16 items-center justify-between">
                <div>
                  <p className={`text-${isScrolled ? "gray-200" : "black"}`}>
                    MyJobs
                  </p>
                </div>
                <div className="h-7 w-4/12 bg-gray-200 outline-none">
                  <input
                    type="Search"
                    placeholder="Search For Jobs"
                    className="outline-none bg-gray-200 items-center w-11/12"></input>
                  <span className="">
                    <i class="fa-solid fa-magnifying-glass"></i>
                  </span>
                </div>
                <div>
                  <p className={`text-${isScrolled ? "gray-200" : "black"}`}>
                    JobListings
                  </p>
                </div>
                <div>
                  <p className={`text-${isScrolled ? "gray-200" : "black"}`}>
                    UserLogin
                  </p>
                </div>
                <div>
                  <p className={`text-${isScrolled ? "gray-200" : "black"}`}>
                    UserRegister
                  </p>
                </div>
              </div>
            </div>
          </nav>
        </header>
        <section>
          <div className="relative pt-50px">
            <div className="flex flex-wrap main-bnr justify-evenly bg-gray-100">
              <div className=" max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
                <div className="flex items-center flex-wrap ">
                  <div className="xl:col-span-7 lg:col-span-7 md:col-span-12">
                    <h6 className="text-blue-400 mb-2 text-xl font-semibold">
                      We Have 208,000+ Live jobs
                    </h6>
                    <h1 className="text-7xl mb-5 font-semibold">
                      Your
                      <span className="text-blue-600 font-semibold">
                        {" "}
                        Dream{" "}
                      </span>
                      Job Is <br></br>Waiting For You
                    </h1>
                    <p className="text-xl font-semibold text-blue-500">
                      Type your keywork,then click search to find your perfect
                      job
                    </p>
                    <div className="m-4 w-full flex h-20 items-center bg-white rounded-xl shadow-xl mb-20px">
                      <div className="items-center">
                        <span className="text-blue-500 ml-6 mr-3 font-semibold">
                          <i class="fa-solid fa-magnifying-glass"></i>
                        </span>
                        <input
                          type="search"
                          placeholder="Job Titles,Keywords..."
                          className="outline-none"></input>
                      </div>
                      <div className="h-7 bg-gray-400 w-0.5 mr-6"></div>
                      <div>
                        <span className="text-blue-600 mr-3">
                          <i class="fa-solid fa-location-dot"></i>
                        </span>
                        <input
                          type="search"
                          placeholder="City or Postcode"
                          className="outline-none"></input>
                      </div>
                      <div className="bg-blue-500 h-10 flex items-center justify-center text-white rounded-md w-1/5">
                        <button className="">Find Jobs</button>
                      </div>
                    </div>
                    <div className="ml-4">
                      <p className="text-gray-400">
                        <span className="font-bold text-black">
                          Popular Searches:
                        </span>
                        <span> Designer,Senor,Architecture,IOS,Etc.</span>
                      </p>
                    </div>
                  </div>
                  <div className="xl:col-5 lg:col-5 md:col-12">
                    <div className="Banner">
                      <img
                        src="https://jobick.dexignlab.com/frontend/xhtml/assets/images/home-banner/media-men.png"
                        alt="Myimage"
                        className="max-w-full align-middle"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        <section className="inner-content">
          <About />
        </section>
        <section>
          <JobsCategory />
        </section>
        <section className="inner-content bg-gray-100">
          <div className="container max-w-7xl mx-auto px-4">
            <div className="text-center mb-7">
              <h6 className="text-blue-500 font-semibold mb-2 text-lg">
                All Jobs Post
              </h6>
              <h2 className="text-6xl font-semibold mb-2">
                Find Your Career You Deserve It
              </h2>
            </div>
            <div>
              <JobsGrid />
            </div>
          </div>
        </section>
        <section className="inner-content4">
          <div className="container max-w-7xl mx-auto px-4">
            <div className="text-center mb-7">
              <h6 className="text-blue-500 font-semibold mb-2 text-lg">
                Client Testimonials
              </h6>
              <h2 className="text-6xl font-semibold mb-2">
                What A Job Holder Says About Us
              </h2>
            </div>
            <Swiper
              spaceBetween={30}
              slidesPerView={3}
              navigation
              pagination={{ clickable: true }}
              onSlideChange={() => console.log("slide change")}
              onSwiper={(swiper) => console.log(swiper)}>
              {testimonials.map((testimonial) => (
                <SwiperSlide key={testimonial.id}>
                  <div className="relative bg-blue-100 m-2 rounded-lg">
                    <div className="block">
                      <p>{testimonial.quote}</p>
                    </div>
                    <div className="flex">
                      <div>
                        <img
                          src={testimonial.image}
                          alt={testimonial.name}
                          className="rounded-full w-20 h-20 mb-2"
                        />
                      </div>
                      <div>
                        <h3>{testimonial.name}</h3>
                        <h5>{testimonial.jobPosition}</h5>
                      </div>
                    </div>
                  </div>
                </SwiperSlide>
              ))}
            </Swiper>
          </div>
        </section>
      </div>
    </>
  );
}

export default Navbar;
