import React, { useContext } from "react";
import { DataContext } from "../Contexts/Data-context";
function JobsGrid() {
  const Jobs = useContext(DataContext);
  return (
    <div>
      <div className="grid grid-cols-3 gap-4">
        <div className="grid grid-cols-3 gap-4">
          {jobs.map((job) => (
            <div className="bg-white p-4 rounded-lg job-box" key={job._id}>
              <div className="job-media mb justify-between flex">
                <span>
                  <img
                    src="https://via.placeholder.com/50"
                    alt="Company Icon" // Replace with actual company icon if available in the API
                  />
                </span>
                <ul className="flex justify-between text-sm">
                  <li className="text-blue-500">
                    {new Date(job.postedAt).toDateString()}
                  </li>
                  <li className="text-blue-500 bg-blue-100 rounded-lg flex justify-center items-center">
                    {job.location}
                  </li>
                </ul>
              </div>
              <div className="job-title">
                <h3>Title: {job.title}</h3>
                <p>{job.description}</p>
              </div>
              <div className="job-amount">
                <h6>Salary: {job.salary}</h6>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default JobsGrid;
