import React, { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    // Check local storage for the token on initial render
    return localStorage.getItem("token") !== null;
  });
  const [token, setToken] = useState(localStorage.getItem("token")); // Store token state

  const login = (newToken) => {
    setIsAuthenticated(true);
    setToken(newToken);
    localStorage.setItem("token", newToken); // Store token in local storage
  };

  const logout = () => {
    setIsAuthenticated(false);
    setToken(null);
    localStorage.removeItem("token"); // Remove token from local storage
  };

  const checkAuthStatus = () => {
    // Check if token exists in local storage
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token); // Set authenticated state based on token presence
    setToken(token); // Update token state
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, token, login, logout ,checkAuthStatus }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
